import React, { Component } from 'react'
import Die from './Die';

export default class RollDice extends Component {
    static defaultProps = {
        sides: ['one','two','three','four','five','six']
    }
    constructor(props) {
        super(props);
        this.state = {
            die1: 'one',
            die2: 'one',
        }
    }

    diceRoll = () => {
        let randIdx = this.props.sides[Math.floor(Math.random() * this.props.sides.length)]
        const NewDie1 = randIdx;
        const NewDie2 = randIdx;
        this.setState ({die1: NewDie1, die2: NewDie2})
    }
  render() {
    return (
      <div>
        <Die face={this.state.die1}/>
        <Die face={this.state.die2}/>
        <button type='button' onClick={()=> this.diceRoll()} >Roll Dice</button>
      </div>
    )
  }
}
